package com.innomob;


public class Day2 {

    static String input1 = "";
    static String input2 = "";
    static String input3 = "";
    static String input4 = "";
    static String input5 = "";


    public static void main(String[] args) {
        System.out.println(calcNumber(input1));
        System.out.println(calcNumber(input2));
        System.out.println(calcNumber(input3));
        System.out.println(calcNumber(input4));
        System.out.println(calcNumber(input5));

    }


    static int calcNumber(String in) {
        int[] coord = {1, 1};
        for (char c : in.toCharArray()) {
            processStep(c, coord);
        }
        return coord[0] + 1 + coord[1] * 3;
    }

    static void processStep(char c, int[] coord) {
        switch (c) {
            case 'L':
                coord[0]--;
                if (coord[0] < 0) {
                    coord[0] = 0;
                }
                break;
            case 'R':
                coord[0]++;
                if (coord[0] > 2) {
                    coord[0] = 2;
                }
                break;
            case 'U':
                coord[1]--;
                if (coord[1] < 0) {
                    coord[1] = 0;
                }
                break;
            case 'D':
                coord[1]++;
                if (coord[1] > 2) {
                    coord[1] = 2;
                    break;

                }
        }
    }
}