package com.innomob;


import java.util.ArrayList;
import java.util.List;

public class Day2P2 {

    static String input1 = "";
    static String input2 = "";
    static String input3 = "";
    static String input4 = "";
    static String input5 = "";

    static List<String> forbiddenCoords = new ArrayList<String>();

    static int[] coord = {0, 2};


    public static void main(String[] args) {

        forbiddenCoords.add("0,0");
        forbiddenCoords.add("0,1");
        forbiddenCoords.add("1,0");
        forbiddenCoords.add("0,3");
        forbiddenCoords.add("0,4");
        forbiddenCoords.add("1,4");
        forbiddenCoords.add("3,0");
        forbiddenCoords.add("4,0");
        forbiddenCoords.add("4,1");
        forbiddenCoords.add("4,3");
        forbiddenCoords.add("3,4");
        forbiddenCoords.add("4,4");

        calcNumber(input1);
        calcNumber(input2);
        calcNumber(input3);
        calcNumber(input4);
        calcNumber(input5);

    }


    static int calcNumber(String in) {

        for (char c : in.toCharArray()) {
            processStep(c);
        }
        System.out.println(coord[0] + " : " + coord[1]);
        return coord[0] + 1 + coord[1] * 3;
    }

    static void processStep(char c) {
        int coord0Save = coord[0];
        int coord1Save = coord[1];
        switch (c) {
            case 'L':
                coord[0]--;
                break;
            case 'R':
                coord[0]++;
                break;
            case 'U':
                coord[1]--;
                break;
            case 'D':
                coord[1]++;
                break;
        }
        if (coord[0] < 0 || coord[1] < 0 || coord[0] > 4 || coord[1] > 4 || forbiddenCoords.contains(coord[0] + ","
                + coord[1])) {
            coord[0] = coord0Save;
            coord[1] = coord1Save;
        }
    }
}